 javascript
  makeNewRows() {
   return this._getAllWords(this.getPipishInput())
  }
  _getAllWords(text) {
   const rows = []
   if (!text) return rows
   const words = text
    .split(/\\s/g)
    .map(word => word.replace(/[^a-z0-9\\-]/gi, ""))
    .filter(word => word)
   const index = {}
   words.forEach(word => {
    if (!index[word]) index[word] = 1
    else index[word]++
   })
   Object.keys(index).forEach(word => {
    const trimmedWord = word.trim()
    if (trimmedWord)
     rows.push({
      word: trimmedWord,
      count: index[trimmedWord]
     })
   })
   return rows
  }
