CodeMirrorConstants.keyMap = {}
CodeMirrorConstants.keyMap.cmdEnter = "Cmd-Enter"
CodeMirrorConstants.keyMap.shiftCmdEnter = "Shift-Cmd-Enter"
CodeMirrorConstants.keyMap.cmdBackSlash = "Cmd-\\"
